import { combineReducers } from "redux";
 import { modal } from "./modal";
 import { aside } from "./aside";
 import { map } from "./map";
 import { desctiption } from "./desctiption";



// собирает все reducers в 1
export const rootReducer = combineReducers({
    modal,
    aside,
    map,
    desctiption
});
